mapper.select:
SELECT
admission.admission_nm, attend.type_nm,
attend.attend_date, attend.attend_time,
examinee.examinee_cd, examinee.examinee_nm, examinee.birth, examinee.major_nm, examinee.dept_nm,
CASE WHEN attend_hall.hall_cd IS NULL THEN false
ELSE true
END AS is_attend,
hall.head_nm, hall.bldg_nm, hall.hall_nm,
attend_hall.head_nm AS attend_head_nm,
attend_hall.bldg_nm AS attend_bldg_nm,
attend_hall.hall_nm AS attend_hall_nm,
attend_hall.is_etc,
attend_map.is_check,
CASE WHEN attend_map.attend_hall_cd IS NULL THEN NULL
WHEN attend_map.attend_hall_cd != attend_map.hall_cd THEN true
ELSE false
END AS is_other_hall,
attend_map.attend_dttm,
(SELECT paper_cd FROM attend_paper WHERE attend_paper.attend_cd = attend_map.attend_cd and
attend_paper.examinee_cd = attend_map.examinee_cd and attend_paper.paper_no = 1 ORDER BY reg_dttm DESC LIMIT 1)
AS last_paper_cd,
CASE WHEN (SELECT COUNT(*) FROM attend_paper WHERE attend_paper.attend_cd = attend_map.attend_cd and
attend_paper.examinee_cd = attend_map.examinee_cd and attend_paper.paper_no = 1) > 1 THEN 1 ELSE 0 END AS is_change_paper
FROM
attend_map
INNER JOIN attend ON attend.attend_cd = attend_map.attend_cd
INNER JOIN admission ON admission.admission_cd = attend.admission_cd
INNER JOIN hall ON hall.hall_cd = attend_map.hall_cd
INNER JOIN examinee ON examinee.examinee_cd = attend_map.examinee_cd
LEFT JOIN hall attend_hall ON attend_hall.hall_cd = attend_map.attend_hall_cd


        AND admission.admission_nm = #{param.admissionNm}
        AND attend.type_nm = #{param.typeNm}
        AND attend.attend_date = #{param.attendDate}
        AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T')
        AND examinee.examinee_cd = #{param.examineeCd}
        AND examinee.examinee_nm = #{param.examineeNm}
        AND attend_hall.head_nm = #{param.attendHeadNm}
        AND attend_hall.bldg_nm = #{param.attendBldgNm}
        AND attend_hall.hall_nm = #{param.attendHallNm}
        AND hall.head_nm = #{param.headNm}
        AND examinee.dept_nm = #{param.deptNm}
        AND examinee.major_nm = #{param.majorNm}
        AND hall.bldg_nm = #{param.bldgNm}
        AND hall.hall_nm = #{param.hallNm}
        AND attend.attend_nm = #{param.attendNm}
        AND attend_hall.is_etc = #{param.isEtc}
        AND attend_map.group_nm = #{param.groupNm}

            AND attend_map.attend_hall_cd IS NOT NULL
            AND attend_map.attend_hall_cd IS NULL


            AND (attend_map.attend_hall_cd IS NOT NULL AND
                attend_map.attend_hall_cd != attend_map.hall_cd)

            AND (attend_map.attend_hall_cd IS NOT NULL AND
                attend_map.attend_hall_cd = attend_map.hall_cd)


        AND attend_map.examinee_cd =
            #{param.fromExamineeCd}

        AND attend_map.examinee_cd =
            #{param.toExamineeCd}


            AND examinee.examinee_cd BETWEEN #{param.fromExamineeCd} AND #{param.toExamineeCd}

        AND attend_map.is_no_id_card = #{param.isNoIdCard}
        AND attend_map.is_check = #{param.isCheck}


GROUP BY
admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time,
examinee.examinee_cd, examinee.examinee_nm, examinee.birth, examinee.major_nm, examinee.dept_nm,
is_attend, hall.head_nm, hall.bldg_nm, hall.hall_nm,
attend_head_nm, attend_bldg_nm, attend_hall_nm,
attend_hall.is_etc, attend_map.is_check, is_other_hall, attend_map.attend_dttm

SELECT examinee.dept_nm, examinee.major_nm, examinee.examinee_cd, examinee.examinee_nm, attend_paper.paper_no, (SELECT COUNT(*) FROM attend_paper A WHERE A.attend_cd = attend_paper.attend_cd and A.examinee_cd = examinee.examinee_cd and A.paper_no = attend_paper.paper_no) AS paper_cnt, attend_paper.paper_cd, attend_paper.reg_dttm FROM attend_paper JOIN examinee ON examinee.examinee_cd = attend_paper.examinee_cd
examinee.examinee_cd = #{param.examineeCd}
GROUP BY examinee.dept_nm, examinee.major_nm, examinee.examinee_cd, attend_paper.paper_no, attend_paper.paper_cd
