mapper.select:
SELECT admission.admission_nm , attend.type_nm, attend.attend_date, attend.attend_time , examinee.dept_nm, examinee.major_nm, examinee.examinee_cd, examinee.examinee_nm, DATE_FORMAT(examinee.birth, '%y-%m-%d') AS birth , hall.head_nm, hall.bldg_nm, hall.hall_nm , ah.head_nm AS attend_head_nm, ah.bldg_nm AS attend_bldg_nm, ah.hall_nm AS attend_hall_nm , IF(attend_map.attend_hall_cd IS NULL, FALSE, TRUE) AS is_attend , IF(attend_map.attend_hall_cd IS NOT NULL, CASE attend_map.is_scanner WHEN TRUE THEN TRUE WHEN FALSE THEN FALSE WHEN NULL THEN NULL END, NULL) AS is_examinee_cd_scanner , IF((SELECT COUNT(*) FROM attend_paper WHERE attend_map.attend_cd = attend_cd AND attend_map.examinee_cd = examinee_cd) > 1, TRUE, FALSE) AS is_change_paper , IF(attend_map.attend_hall_cd IS NOT NULL, (select paper_cd from attend_paper where attend_paper.attend_cd = attend_map.attend_cd and attend_paper.examinee_cd = attend_map.examinee_cd order by reg_dttm desc limit 1), NULL) as last_paper_cd , IF(attend_map.attend_hall_cd IS NOT NULL, CASE (select is_scanner from attend_paper where attend_paper.attend_cd = attend_map.attend_cd and attend_paper.examinee_cd = attend_map.examinee_cd order by reg_dttm desc limit 1) WHEN TRUE THEN TRUE WHEN FALSE THEN FALSE WHEN NULL THEN NULL END, NULL) AS is_paper_cd_scanner -- 출력부분 , examinee.exm_adm_nm , examinee.college_nm , now() AS printDttm FROM attend_map INNER JOIN attend ON attend_map.attend_cd = attend.attend_cd INNER JOIN admission ON attend.admission_cd = admission.admission_cd INNER JOIN examinee ON attend_map.examinee_cd = examinee.examinee_cd INNER JOIN hall ON attend_map.hall_cd = hall.hall_cd LEFT JOIN hall ah ON attend_map.attend_hall_cd = ah.hall_cd
AND admission.admission_nm = #{param.admissionNm} AND attend.type_nm = #{param.typeNm} AND attend.attend_date = #{param.attendDate} AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T') AND examinee.examinee_cd LIKE '%' #{param.examineeCd} '%' AND examinee.examinee_nm LIKE '%' #{param.examineeNm} '%' AND ah.head_nm = #{param.attendHeadNm} AND ah.bldg_nm = #{param.attendBldgNm} AND ah.hall_nm = #{param.attendHallNm} AND hall.head_nm = #{param.headNm} AND hall.bldg_nm = #{param.bldgNm} AND (hall.hall_nm = #{param.hallNm} OR ah.hall_nm = #{param.hallNm}) AND examinee.dept_nm = #{param.deptNm} AND examinee.major_nm = #{param.majorNm}
AND (SELECT paper_cd FROM attend_paper WHERE attend_cd = attend_map.attend_cd AND examinee_cd = attend_map.examinee_cd ORDER BY reg_dttm DESC LIMIT 1) = #{param.lastPaperCd}
AND attend_hall.is_etc = #{param.isEtc} AND attend_map.group_nm = #{param.groupNm}
AND attend_map.attend_hall_cd IS NOT NULL AND attend_map.attend_hall_cd IS NULL
AND (attend_map.attend_hall_cd IS NOT NULL AND attend_map.attend_hall_cd != attend_map.hall_cd) AND (attend_map.attend_hall_cd IS NOT NULL AND attend_map.attend_hall_cd = attend_map.hall_cd)
AND (SELECT COUNT(*)%2 FROM attend_map_log WHERE attend_map_log.attend_cd = attend_map.attend_cd AND attend_map_log.examinee_cd = attend_map.examinee_cd) = 1 AND attend_map.is_scanner IS NOT NULL AND attend_dttm IS NULL
AND attend_map.attend_hall_cd IS NOT NULL AND attend_map.is_scanner = 1 AND attend_map.is_scanner = 0
AND attend_map.attend_hall_cd IS NOT NULL AND (select is_scanner from attend_paper where attend_paper.attend_cd = attend_map.attend_cd and attend_paper.examinee_cd = attend_map.examinee_cd order by reg_dttm desc limit 1) = TRUE AND (select is_scanner from attend_paper where attend_paper.attend_cd = attend_map.attend_cd and attend_paper.examinee_cd = attend_map.examinee_cd order by reg_dttm desc limit 1) = FALSE
AND attend_map.examinee_cd = #{param.fromExamineeCd} AND attend_map.examinee_cd = #{param.toExamineeCd} AND examinee.examinee_cd BETWEEN #{param.fromExamineeCd} AND #{param.toExamineeCd} AND attend.admission_cd IN (${param.userAdmissions})

SELECT admission.admission_nm
    , attend.type_nm, attend.attend_date, attend.attend_time
    , examinee.dept_nm, examinee.major_nm, examinee.examinee_cd, examinee.examinee_nm, date_format(examinee.birth, '%y-%m-%d') as birth
    , a.paper_cnt, a.first_paper_cd, a.last_paper_cd, a.paper_list
FROM ( SELECT attend_cd, examinee_cd, hall_cd
            , COUNT(*) AS paper_cnt
            , (SELECT paper_cd FROM attend_paper_log a WHERE a.attend_cd = attend_paper_log.attend_cd AND a.examinee_cd = attend_paper_log.examinee_cd ORDER BY a.reg_dttm ASC LIMIT 1) AS first_paper_cd
            , (SELECT paper_cd FROM attend_paper_log a WHERE a.attend_cd = attend_paper_log.attend_cd AND a.examinee_cd = attend_paper_log.examinee_cd ORDER BY a.reg_dttm DESC LIMIT 1) AS last_paper_cd
            , GROUP_CONCAT(paper_cd ORDER BY reg_dttm SEPARATOR ',') AS paper_list
            , MAX(reg_dttm) AS reg_dttm
            FROM attend_paper_log
            WHERE attend_paper_log.new_paper_cd is null
            GROUP BY attend_cd, examinee_cd, hall_cd HAVING COUNT(*) > 1) a
INNER JOIN attend ON a.attend_cd = attend.attend_cd
INNER JOIN admission ON attend.admission_cd = admission.admission_cd
INNER JOIN examinee ON a.examinee_cd = examinee.examinee_cd
INNER JOIN hall ON a.hall_cd = hall.hall_cd


        AND admission.admission_nm = #{param.admissionNm}
        AND attend.attend_cd = #{param.attendCd}
        AND attend.type_nm = #{param.typeNm}
        AND attend.attend_date = #{param.attendDate}
        AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T')
        AND examinee.dept_nm = #{param.deptNm}
        AND examinee.major_nm = #{param.majorNm}
        AND examinee.examinee_cd LIKE '%' #{param.examineeCd} '%'
        AND examinee.examinee_nm LIKE '%' #{param.examineeNm} '%'
        AND a.paper_list LIKE '%' #{param.paperCd} '%'
        AND hall.head_nm = #{param.headNm}
        AND hall.bldg_nm = #{param.bldgNm}
        AND hall.hall_cd = #{param.hallCd}
        AND hall.hall_nm = #{param.hallNm}
        AND attend.admission_cd IN (${param.userAdmissions})

mapper.update:
UPDATE attend_manage
   SET id_check_dttm = #{idCheckDttm}
 WHERE examinee_cd = #{examineeCd} and attend_cd = #{attendCd}
