mapper.select:
SELECT admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time, examinee.dept_nm, COUNT(*) AS examinee_cnt, COUNT (attend_map.attend_hall_cd) AS attend_cnt, COUNT (attend_map.attend_hall_cd) * 100 / COUNT (*) AS attend_per, COUNT (*) - COUNT (attend_map.attend_hall_cd) AS absent_cnt, ( COUNT (*) - COUNT (attend_map.attend_hall_cd)) * 100 / COUNT (*) AS absent_per FROM attend_map INNER JOIN attend ON attend.attend_cd = attend_map.attend_cd INNER JOIN admission ON admission.admission_cd = attend.admission_cd INNER JOIN examinee ON examinee.examinee_cd = attend_map.examinee_cd
AND admission.admission_nm = #{param.admissionNm} AND attend.type_nm = #{param.typeNm} AND attend.attend_date = #{param.attendDate} AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T') AND examinee.dept_nm = #{param.deptNm}
GROUP BY admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time, examinee.dept_nm

SELECT admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time, examinee.dept_nm, examinee.major_nm, COUNT(*) AS examinee_cnt, COUNT (attend_map.attend_hall_cd) AS attend_cnt, COUNT (attend_map.attend_hall_cd) * 100 / COUNT (*) AS attend_per, COUNT (*) - COUNT (attend_map.attend_hall_cd) AS absent_cnt, ( COUNT (*) - COUNT (attend_map.attend_hall_cd)) * 100 / COUNT (*) AS absent_per FROM attend_map INNER JOIN attend ON attend.attend_cd = attend_map.attend_cd INNER JOIN admission ON admission.admission_cd = attend.admission_cd INNER JOIN examinee ON examinee.examinee_cd = attend_map.examinee_cd
AND admission.admission_nm = #{param.admissionNm} AND attend.type_nm = #{param.typeNm} AND attend.attend_date = #{param.attendDate} AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T') AND examinee.dept_nm = #{param.deptNm} AND examinee.major_nm = #{param.majorNm}
GROUP BY admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time, examinee.dept_nm, examinee.major_nm

SELECT * , (examinee_cnt - to_other_cnt + from_other_cnt) AS current_cnt , (A + from_other_cnt) AS attend_cnt , (A + from_other_cnt) * 100 / (examinee_cnt - to_other_cnt + from_other_cnt) AS attend_per , (examinee_cnt - to_other_cnt + from_other_cnt) - (A + from_other_cnt) AS absent_cnt , 100 - ((A + from_other_cnt) * 100 / (examinee_cnt - to_other_cnt + from_other_cnt)) AS absent_per FROM (SELECT admission.admission_nm , attend.type_nm, attend.attend_date, attend.attend_time , hall.head_nm, hall.bldg_nm, hall.hall_nm , (SELECT COUNT(*) FROM attend_map WHERE attend_map.attend_cd = attend.attend_cd AND attend_map.hall_cd = hall.hall_cd) AS examinee_cnt , (SELECT COUNT(*) FROM attend_map WHERE attend_map.attend_cd = attend.attend_cd AND attend_map.hall_cd = hall.hall_cd AND attend_map.hall_cd = attend_map.attend_hall_cd) AS A , (SELECT COUNT(*) FROM attend_map WHERE attend_map.attend_cd = attend.attend_cd AND attend_map.attend_hall_cd = hall.hall_cd AND attend_map.hall_cd != attend_map.attend_hall_cd) AS from_other_cnt , (SELECT COUNT(*) FROM attend_map WHERE attend_map.attend_cd = attend.attend_cd AND attend_map.hall_cd = hall.hall_cd AND attend_map.hall_cd != attend_map.attend_hall_cd) AS to_other_cnt FROM attend_hall INNER JOIN attend ON attend.attend_cd = attend_hall.attend_cd INNER JOIN admission ON admission.admission_cd = attend.admission_cd INNER JOIN hall ON hall.hall_cd = attend_hall.hall_cd
AND admission.admission_nm = #{param.admissionNm} AND attend.type_nm = #{param.typeNm} AND attend.attend_date = #{param.attendDate} AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T') AND hall.head_nm = #{param.headNm} AND hall.bldg_nm = #{param.bldgNm} AND hall.hall_nm = #{param.hallNm}
) AS result

SELECT admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time, examinee.major_nm, attend_map.group_nm, hall.head_nm, hall.bldg_nm, hall.hall_nm, COUNT(*) AS examinee_cnt, COUNT (attend_map.attend_hall_cd) AS attend_cnt, COUNT (attend_map.attend_hall_cd) * 100 / COUNT (*) AS attend_per, COUNT (*) - COUNT (attend_map.attend_hall_cd) AS absent_cnt, ( COUNT (*) - COUNT (attend_map.attend_hall_cd)) * 100 / COUNT (*) AS absent_per FROM attend_map INNER JOIN attend ON attend.attend_cd = attend_map.attend_cd INNER JOIN admission ON admission.admission_cd = attend.admission_cd INNER JOIN examinee ON examinee.examinee_cd = attend_map.examinee_cd INNER JOIN hall ON hall.hall_cd = attend_map.hall_cd
AND admission.admission_nm = #{param.admissionNm} AND attend.type_nm = #{param.typeNm} AND attend.attend_date = #{param.attendDate} AND attend.attend_time = DATE_FORMAT(#{param.attendTime}, '%T') AND examinee.major_nm = #{param.majorNm} AND attend_map.group_nm LIKE '%' #{param.groupNm} '%' AND hall.head_nm = #{param.headNm} AND hall.bldg_nm = #{param.bldgNm} AND hall.hall_nm = #{param.hallNm}
GROUP BY admission.admission_nm, attend.type_nm, attend.attend_date, attend.attend_time, attend_map.group_nm, hall.head_nm, hall.bldg_nm, hall.hall_nm, examinee.major_nm
