mapper.select:
select admission.admission_nm, attend.*
, case when (select GROUP_CONCAT(attend_hall.group_nm_list separator ';')) is null then false else true end as isEmptyHall
from attend
inner join admission on admission.admission_cd = attend.admission_cd
inner join attend_hall on attend_hall.attend_cd = attend.attend_cd


        and admission.admission_nm = #{param.admissionNm}
        and attend.attend_cd = #{param.attendCd}
        and attend.attend_nm = #{param.attendNm}


group by attend.attend_cd

select distinct attend.* from attend


        and attend.attend_cd = #{param.attendCd}


group by attend.attend_cd

update.if:
update attend set attend_nm = #{param.attendNm}, type_nm = #{param.typeNm}, attend_len = #{param.attendLen}
, attend_date = #{param.atDate}, attend_time = #{param.atTime}, is_paper_change = #{param.isPaperChange}
, is_use_group = #{param.isUseGroup}, paper_cnt = #{param.paperCnt}, paper_len = #{param.paperLen}, paper_header = #{param.paperHeader}
, is_assigned_group = #{param.isAssignedGroup}, first_assign_paper_cd = #{param.firstAssignPaperCd}, last_assign_paper_cd = #{param.lastAssignPaperCd}
where attend_cd = #{param.attendCd}

UPDATE attend_hall set group_nm_list = #{param.groupNmList}
WHERE attend_cd = #{param.attendCd}
and _id = #{param.id}
and hall_cd = #{param.hallCd}

mapper.select:
select examinee.examinee_cd, examinee.examinee_nm, device.device_id, device.device_no , case when attend_map_log.attend_dttm is not null then true else false end as current_state , attend_map_log.log_dttm, hall.bldg_nm, hall.hall_nm from attend_map_log inner join examinee on examinee.examinee_cd = attend_map_log.examinee_cd left join device on device.device_id = attend_map_log.device_id left join hall on hall.hall_cd = attend_map_log.attend_hall_cd
and attend_map_log.attend_cd = #{param.attendCd} and attend_map_log.examinee_cd = #{param.examineeCd}
order by attend_map_log.log_dttm desc

mapper.update:
update staff set staff_nm = #{param.staffNm}, phone_no = #{param.phoneNo}, attend_cd = #{param.attendCd}


         and staff_nm = #{param._staffNm}
         and phone_no = #{param._phoneNo}
         and bldg_nm = #{param._bldgNm}
         and attend_cd = #{param._attendCd}

delete from staff


         and staff_nm = #{param._staffNm}
         and phone_no = #{param._phoneNo}
         and bldg_nm = #{param._bldgNm}
         and attend_cd = #{param._attendCd}
